delimiter ??
CREATE PROCEDURE `add_migration`()
BEGIN
DECLARE v INT DEFAULT 1;
SET v = (SELECT COUNT(*) FROM `migrations` WHERE `id`='20171104204034');
IF v=0 THEN
INSERT INTO `migrations` VALUES ('20171104204034');
-- Add your query below.


-- Fixes Neeru Fireblade's gossip for quest Hidden Enemies.
