INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (3701, 1, '저한테는 솔직하게 얘기를 해도 괜찮습니다, 네루.', 'Vous pouvez parler franchement, Neeru...', 'Ihr dürft frei sprechen, Neeru...', '你明说吧，尼尔鲁……', '你明說吧，尼爾魯...', 'Puedes ser sincero, Neeru...', 'Puedes ser sincero, Neeru...', 'Говори свободно, Ниру...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (3702, 2, '어둠의 의회가 실패한 일을 불타는 칼날단이라도 대신하는 걸 보니 기분이 참 좋군요.', 'Il est bon de voir que la Lame ardente est en train de réussir là où le Conseil des ombres a échoué.', 'Es tut gut zu sehen, wie die Brennende Klinge dort das Szepter übernimmt, wo der Schattenrat einst versagt hat.', '很高兴看到火刃接管了那些暗影议会无力接管的地方。', '很高興看到燃刃接管了那些暗影議會無力接管的地方。', 'Es estupendo ver que el Filo Ardiente tiene éxito donde falló el Consejo de la Sombra.', 'Es estupendo ver que el Filo Ardiente tiene éxito donde falló el Consejo de la Sombra.', 'Хорошо, что Пылающий Клинок побеждает там, где Совет Теней потерпел поражение.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (3703, 3, '이글거리는 칼날단을 이용해도 된단 말이지요?', 'Alors comme ça, la Lame brûlante est sacrifiable ?', 'Die Sengende Klinge ist also entbehrlich?', '这么说，燃刃可以牺牲了？', '這麼說，灼刃可以犧牲了?', '¿Así que la Hoja Abrasadora es prescindible?', '¿Así que la Hoja Abrasadora es prescindible?', 'Значит, Пламенеющий Клинок – лишь орудие?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (3704, 4, '제가 성공해 돌아온다면...', 'Si je peux faire quelque chose pour vous...', 'Wenn es irgendetwas gibt, das ich für Euch tun kann...', '要是你能从我这儿拿走什么的话……', '要是你能從我這兒拿走什麼的話...', 'Si necesitas algo de mí...', 'Si necesitas algo de mí...', 'Чем могу быть полезен?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);



