UPDATE `broadcast_text` SET `MaleText`='Finally. The bastard got what he deserved.' WHERE `ID`=1376;
UPDATE `broadcast_text` SET `MaleText`='Director Riddlevox put me in charge of the Union\'s shredder development program. Few months on the job and I\'m already in hot water! I could use your help, $n!' WHERE `ID`=2461;
